package core

import (
	"math/big"

	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/core/types"
	"github.com/ethereum/go-ethereum/core/vm"
)

// ##CROSS: fee log
var (
	transferLogSig    = common.HexToHash("0xc5f2c798e181bf629a094a23623755616f721d08a274b4b6b1aadb706ef8d3aa")
	crosstokenAddress = common.HexToAddress("0x000000000000000000000000000000000000c255")
	bigZero           = big.NewInt(0)
)

func crosstokenCode() []byte {
	return common.Hex2Bytes("")
}

// AddTransferLog adds transfer log into state
func AddTransferLog(
	state vm.StateDB,
	sender, recipient common.Address,
	amount,
	beforeFrom, beforeTo, afterFrom, afterTo *big.Int,
) {
	// ignore if amount is 0
	if amount.Cmp(bigZero) <= 0 {
		return
	}

	dataInputs := []*big.Int{
		amount,
		beforeFrom,
		beforeTo,
		afterFrom,
		afterTo,
	}

	var data []byte
	for _, v := range dataInputs {
		data = append(data, common.LeftPadBytes(v.Bytes(), 32)...)
	}

	// add transfer log
	state.AddLog(&types.Log{
		Address: crosstokenAddress,
		Topics: []common.Hash{
			transferLogSig,
			common.BytesToHash(crosstokenAddress.Bytes()),
			common.BytesToHash(sender.Bytes()),
			common.BytesToHash(recipient.Bytes()),
		},
		Data: data,
	})
}
